var tryyApp = angular.module("tryyApp", ['ngRoute']);

tryyApp.config(['$httpProvider', '$qProvider', '$compileProvider', '$routeProvider', '$locationProvider', function ($httpProvider, $qProvider, $compileProvider, $routeProvider, $locationProvider) {
	$httpProvider.defaults.headers.common   = {};
	$httpProvider.defaults.headers.post     = {};
	$httpProvider.defaults.headers.put      = {};
	$httpProvider.defaults.headers.patch    = {};
	$httpProvider.defaults.useXDomain 		= true;
	$httpProvider.defaults.withCredentials  = false;
	
	$qProvider.errorOnUnhandledRejections(false);
  	$compileProvider.debugInfoEnabled(false);  	

	//check browser support
  	if(window.history && window.history.pushState){
	    // $locationProvider.html5Mode(true); will cause an error $location in HTML5 mode requires a  tag to be present! Unless you set baseUrl tag after head tag like so: <head> <base href="/">
		// to know more about setting base URL visit: https://docs.angularjs.org/error/$location/nobase
		// if you don't wish to set base URL then use this
		$locationProvider.html5Mode({
			enabled: true,
			requireBase: true
		}).hashPrefix('!');
  	}  	

  	// main routes of website
  	$routeProvider

  	// home
  	.when('/', {
		templateUrl	: 'views/home.html',
		controller 	: 'homeCtrl'
    })
    .when('/features', {
		templateUrl	: 'views/features.html',
		controller 	: 'featuresCtrl'
    })
    .when('/fitnshop', {
		templateUrl	: 'views/fitshop.html',
		controller 	: 'fitShopCtrl'
    })
    .when('/about', {
		templateUrl	: 'views/about.html',
		controller 	: 'aboutCtrl'
    })
    .when('/contact', {
		templateUrl	: 'views/contact.html',
		controller: 'contactCtrl'
    })
    .otherwise({
	    redirectTo: '/'
	});
}]);

